import React, { useState, useEffect } from "react";
import { For } from 'react-loops'
import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import Accordion from 'react-bootstrap/Accordion'
import Card from 'react-bootstrap/Card'
import Modal from 'react-bootstrap/Modal'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import ProfilDataService from '../services/ProfilService'
import { useSelector } from "react-redux";
import './Profil.css'

const Profil = props => {

    const [listeOptions, setListeOptions] = useState(0)
    const [modalShow, setModalShow] = useState(false);
    const [activeOnglet, setActiveOnglet] = useState();
    const [profil, setProfil] = useState();

    const listeOnglets = ['Type de Gestion',
        'Profil risque gain',
        'Support d\'investissement',
        'Marchés & Produits',
        'Règles d\'Or',
        'Pour aller plus loin']

    const nbPointProfil = useSelector((state) => {
        return state.profil.point
    });

    const nbQuestionProfil = useSelector((state) => {
        return state.profil.nbReponses

    });
    const ResultatProfil = Number((nbPointProfil / nbQuestionProfil).toFixed(0));

    const getActiveBloc = (onglet) => {
        setActiveOnglet(onglet)
        getProfil()
        console.log(profil)
        // getOptions(bloc.id)
        // console.log(listeOptions)

    }

    const getProfil = () => {
        ProfilDataService.getById(ResultatProfil)
            .then(response => {
                setProfil(response.data);
            })
            .catch(e => {
                console.log(e)
            })
    }



    const MydModalWithGrid = props => {
        return (
            <Modal {...props}
                dialogClassName="modal-bloc"
                aria-labelledby="contained-modal-title-vcenter"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {activeOnglet ? <p>{e.value}</p> : <p>NADA</p>}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body className="modal-body">
                    <Container className="container">
                        <Row className="show-grid">

                            <Col xs={6} md={4}>
                                Liens utiles :
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                        </Row>

                        <Row className="show-grid">
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                        </Row>
                    </Container>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide} className="btn-close">Fermer</Button>
                </Modal.Footer>
            </Modal>
        );
    }


    return (
        <div>
            <h1>RESULTATS</h1>
            <p>Profil n° : {ResultatProfil}</p>
            <>
                <div className="resultCard">
                    <Button className="bloc-btn" value="Profil" onClick={(e) => {
                        setModalShow(true);
                        // getActiveBloc(onglet)
                    }}>
                        Profil
                </Button>
                    <Button className="bloc-btn" onClick={() => {
                        setModalShow(true);
                        // getActiveBloc(onglet)
                    }}>
                        Salut
                </Button>
                </div>

                {/* <For of={listeOnglets} as={onglet =>
                    <div className="resultCard">
                        <Button className="bloc-btn" onClick={() => {
                            setModalShow(true);
                            getActiveBloc(onglet)
                        }}>
                            {onglet}
                        </Button>
                    </div>
                } /> */}
                <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)} />
            </>
        </div>
    );
};

export default Profil;
