import * as types from "../constants/profilActionTypes";
import { useSelector, useDispatch } from "react-redux";


export const setPointProfil = (pointProfil) => ({
    type: types.ADD_POINT_PROFIL,
    pointProfil: pointProfil
});

const nbPoint = useSelector((state) => {
    return state.profil.pointProfil
});

export function addPointsProfil(pointsProfil) {
    const pointsAvant = nbPoint
    const pointsApres = nbPoint + pointsProfil
    return (dispatch) => {
        dispatch(setPointProfil(pointsApres))
    }
}
