import React, { useState, useEffect } from "react";
import { For } from 'react-loops'
import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import Accordion from 'react-bootstrap/Accordion'
import Card from 'react-bootstrap/Card'
import Modal from 'react-bootstrap/Modal'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import ProfilDataService from '../services/ProfilService'
import { useSelector } from "react-redux";
import ProfilModal from "./modals/profil.modal";
import GestionModal from "./modals/gestion.modal";
import MarcheModal from "./modals/marche.modal";
import PlusLoinModal from "./modals/plusLoin.modal";
import ReglesModal from "./modals/regles.modal";
import SupportModal from "./modals/support.modal";
import './Profil.css'
import ModalBody from "react-bootstrap/ModalBody";

const Profil = props => {

    const [listeOptions, setListeOptions] = useState(0)
    const [modalShow, setModalShow] = useState(false);
    const [activeOnglet, setActiveOnglet] = useState();
    const [profil, setProfil] = useState();

    const listeOnglets = ['Type de Gestion',
        'Profil risque gain',
        'Support d\'investissement',
        'Marchés & Produits',
        'Règles d\'Or',
        'Pour aller plus loin']

    const nbPointProfil = useSelector((state) => {
        return state.profil.point
    });

    const nbQuestionProfil = useSelector((state) => {
        return state.profil.nbReponses

    });
    const ResultatProfil = Number((nbPointProfil / nbQuestionProfil).toFixed(0));

    const getActiveBloc = (onglet) => {
        setActiveOnglet(onglet)
        getProfil()
        console.log(profil)
        // getOptions(bloc.id)
        // console.log(listeOptions)

    }

    const getProfil = () => {
        ProfilDataService.getById(ResultatProfil)
            .then(response => {
                setProfil(response.data);
            })
            .catch(e => {
                console.log(e)
            })
    }



    const MydModalWithGrid = props => {
        return (
            <Modal {...props}
                dialogClassName="modal-bloc"
                aria-labelledby="contained-modal-title-vcenter"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {activeOnglet ? <p>{activeOnglet}</p> : <p>NADA</p>}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {activeOnglet === "Type de Gestion" ? <GestionModal /> : null}
                    {activeOnglet === "Profil risque gain" ? <ProfilModal /> : null}
                    {activeOnglet === "Support d\'investissement" ? <SupportModal /> : null}
                    {activeOnglet === "Marchés & Produits" ? <MarcheModal /> : null}
                    {activeOnglet === "Règles d\'Or" ? <ReglesModal /> : null}
                    {activeOnglet === "Pour aller plus loin" ? <PlusLoinModal /> : null}

                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide} className="btn-close">Fermer</Button>
                </Modal.Footer>
            </Modal>

        );
    }


    return (
        <div>
            <h1>RESULTATS</h1>
            <p>Profil n° : {ResultatProfil}</p>
            <>
                <For of={listeOnglets} as={onglet =>
                    <div className="resultCard">
                        <Button className="bloc-btn" onClick={() => {
                            setModalShow(true);
                            getActiveBloc(onglet)
                        }}>
                            {onglet}
                        </Button>
                    </div>
                } />
                <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)} />
            </>
        </div>
    );
};

export default Profil;
