import * as types from "../constants/profilActionTypes";

export default function profilReducer(state = {}, action) {
    switch (action.type) {
        case types.ADD_POINT_PROFIL:
            return {
                ...state,
                profil: [...state.profil, action.point]
            }
        default:
            return state
    }

}
