import React, { useState, useEffect } from "react";
import { For } from 'react-loops'
import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import Accordion from 'react-bootstrap/Accordion'
import Card from 'react-bootstrap/Card'
import Modal from 'react-bootstrap/Modal'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import ProfilDataService from '../services/ProfilService'
import './Profil.css'

const Profil = props => {

    const [listeOptions, setListeOptions] = useState(0)
    const [modalShow, setModalShow] = useState(false);

    const [activeBloc, setActiveBloc] = useState()

    const getActiveBloc = (bloc) => {
        setActiveBloc(bloc)
        getOptions(bloc.id)
        console.log(listeOptions)

    }

    const getOptions = (id) => {
        ProfilDataService.getOptionsByBloc(id)
            .then(response => {
                setListeOptions(response.data);
            })
            .catch(e => {
                console.log(e)
            })
    }

    const MydModalWithGrid = props => {
        return (
            <Modal {...props}
                dialogClassName="modal-bloc"
                aria-labelledby="contained-modal-title-vcenter"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {activeBloc ? <p>{activeBloc.libelle}</p> : <p>NADA</p>}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body className="modal-body">
                    <Container className="container">
                        <Row className="show-grid">
                            <Col xs={12} md={8}>
                                Liste :
                                {listeOptions ?
                                    <ul>
                                        <For of={listeOptions} as={option =>
                                            <li>{option.libelle}</li>
                                        } />
                                    </ul>
                                    : <p>NADA</p>}

                                {/* <code>.col-xs-12 .col-md-8</code> */}
                            </Col>
                            <Col xs={6} md={4}>
                                Liens utiles :
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                        </Row>

                        <Row className="show-grid">
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                            <Col xs={6} md={4}>
                                {/* <code>.col-xs-6 .col-md-4</code> */}
                            </Col>
                        </Row>
                    </Container>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide} className="btn-close">Fermer</Button>
                </Modal.Footer>
            </Modal>
        );
    }

    return (
        <>
            <For of={props.listeBlocs} as={bloc =>
                <div className="resultCard">
                    <Button className="bloc-btn" onClick={() => {
                        setModalShow(true);
                        getActiveBloc(bloc)
                    }}>
                        {bloc.libelle}
                    </Button>
                </div>
            } />
            <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)} />
        </>
    );
};

export default Profil;
