import React, { useState, useEffect } from "react";
import ActifsDataService from '../../services/ActifServices'
import ProfilDataService from '../../services/ProfilService'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const ModalProfil = props => {

    // const [allActifs, setAllActifs] = useState([])
    const [actifSelected, setActifSelected] = useState([])
    const [allProfils, setAllProfils] = useState([]);

    useEffect(() => {
        getActifSelected()
        getAllProfil()
    }, [])

    const getAllProfil = () => {
        ProfilDataService.getAll()
            .then(response => {
                setAllProfils(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }

    const getActifSelected = () => {
        ActifsDataService.getByProfilId(props.ResultatProfil)
            .then(response => {
                setActifSelected(response.data)
                console.log(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }
    const useStyles = makeStyles({
        root: {
            minWidth: 275,
            marginBottom: 10,
            // backgroundColor: 'green'
        },
        bullet: {
            display: 'inline-block',
            margin: '0 2px',
            transform: 'scale(0.8)',
        },
        title: {
            fontSize: 14,
        },
        pos: {
            marginBottom: 12,
        },
    });
    const classes = useStyles();


    return (
        <div>
            <div>
                Vos réponses permettent de déterminer que vous pouvez opter pour un niveau de risque
            </div>
            {allProfils.map((p, key) => {
                console.log(p)
                return (
                    <Card key={key} className={classes.root} >
                        <CardContent>
                            <Typography className={classes.title} color="textSecondary" gutterBottom>
                                {p.libelle}
                            </Typography>

                            <Typography variant="body2" component="p">
                                Variation moyenne annuelle cible : {p.variation}
                                <br />
                                Temps à y consacrer (Sauf gestion déléguée) : {p.temps}
                                {profil.libelle === "Trader" && p.libelle === "Trader" ?
                                    (<div>
                                        <br />
                                        <p>Vos réponses permettent de déterminer qu'en plus de votre profil d'investisseur vous pouvez envisager le profil Trader</p>
                                    </div>) :
                                    (null)}
                            </Typography>
                        </CardContent>
                    </Card>
                )
            })}

        </div>
    );
};

export default ModalProfil;
