import React, { useState, useEffect } from "react";
import QuestionDataService from "../services/QuestionService"
import Question from "../components/Question"
import Profil from "../components/Profil"
import ProfilDataService from "../services/ProfilService"
import Accueil from "../components/Accueil"
import './Questionnaire.css'

const Questionnaire = props => {

  const [listQuestions, setListQuestions] = useState();
  const [target, setTarget] = useState("accueil");
  const [listeBlocs, setListeBlocs] = useState();

  useEffect(() => getAllBloc(), [target === "profil"])

  const getListQuestions = () => {
    QuestionDataService.getAll()
      .then(response => {
        setListQuestions(response.data);
        setTarget("questionnaire")
      })
      .catch(e => {
        console.log(e)
      })
  }

  const getAllBloc = () => {
    ProfilDataService.getAllBlocs()
    .then(response => {
        setListeBlocs(response.data)
    })
    .catch(e => {
        console.log(e)
    })
}

  const displayProfil = () => {
    setTarget("profil")
  }


  if (target === "profil") {
    return (
      <Profil
        listeBlocs={listeBlocs} />
    )
  }

  if (target === "accueil") {
    return (
      <Accueil
        startQuestionnaire={getListQuestions} />
    )
  }
  
  if (target === "questionnaire") {
    return (
      <Question
        listQuestion={listQuestions}
        displayProfil={displayProfil} />
    )
  }
};



export default Questionnaire;
