import React, { useState, useEffect } from "react";
import ProfilDataService from '../../services/ProfilService'


const ModalProfil = props => {

    const initialStateProfil = {
        id: null,
        libelle: "",
        temps: "",
        variation: "",
        actif_profil: [],
        conseil_profil: [],
        description: ""
    };

    const [profil, setProfil] = useState(initialStateProfil);

    useEffect(() => {
        console.log(props.ResultatProfil)
        getProfil()
    }, [])

    const getProfil = () => {
        ProfilDataService.getById(props.ResultatProfil)
            .then(response => {
                setProfil({
                    id: response.data.id,
                    libelle: response.data.libelle,
                    temps: response.data.temps,
                    variation: response.data.variation,
                    actif_profil: response.data.actif_profil,
                    description: response.data.description
                });
            })
            .catch(e => {
                console.log(e)
            })
    }

    return (
        <Modal {...props}
                dialogClassName="modal-bloc"
                aria-labelledby="contained-modal-title-vcenter"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {activeOnglet ? <p>{activeOnglet}</p> : <p>NADA</p>}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {activeOnglet === "Type de Gestion" ? <GestionModal /> : null}
                    {activeOnglet === "Profil risque gain" ? <ProfilModal ResultatProfil={ResultatProfil} /> : null}
                    {activeOnglet === "Support d\'investissement" ? <SupportModal /> : null}
                    {activeOnglet === "Marchés & Produits" ? <MarcheModal /> : null}
                    {activeOnglet === "Règles d\'Or" ? <ReglesModal /> : null}
                    {activeOnglet === "Pour aller plus loin" ? <PlusLoinModal /> : null}
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide} className="btn-close">Fermer</Button>
                </Modal.Footer>
            </Modal>

    //     <div  >
    //         Vous êtes de profil : {profil.libelle}
    // </div>
    );
};

export default ModalProfil;
