import * as types from "../constants/profilActionTypes";

export default function profil(state = {}, action) {
    switch (action.type) {
        case types.ADD_POINT_PROFIL:
            return {
                ...state,
                point: action.pointProfil
            }
        case types.ADD_RESPONSE_COUNTER:
            return {
                ...state,
                nbReponses: action.nbResponses
            }
        default:
            return state
    }

}
