import * as types from "../constants/profilActionTypes";

// const initialState = {
//     point: Number,
//     nbReponses: Number

// }

export default function profil(state = {nbReponses: 0}, action) {
    switch (action.type) {
        case types.ADD_POINT_PROFIL:
            return {
                ...state,
                point: action.pointProfil
            }
        case types.ADD_RESPONSE_COUNT:
            return {
                ...state,
                nbReponses: state.nbReponses + 1
            }
        default:
            return state
    }

}
