import * as types from "../constants/profilActionTypes";


export const setPointProfil = (pointProfil) => ({
    type: types.ADD_POINT_PROFIL,
    pointProfil : pointProfil,
    nbResponses : nbResponses + 1
});

export function addPointsProfil(pointAvant, pointsAAjouter) {
    const newPoints = pointAvant + pointsAAjouter

    return (dispatch) => {
        dispatch(setPointProfil(newPoints))
    }
}
