import * as types from "../constants/profilActionTypes";

export default function profilReducer(state = 0, action) {
    switch (action.type) {
        case types.ADD_POINT_PROFIL:
            return {
                ...state,
                pointProfil: action.pointProfil
            }
        default:
            return state
    }

}
