import React, { useState, useEffect } from "react";
import ProfilDataService from '../../services/ProfilService'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';


const ModalProfil = props => {



    const useStyles = makeStyles({
        root: {
            minWidth: 275,
            marginBottom:10,
            backgroundColor: green
        },
        bullet: {
            display: 'inline-block',
            margin: '0 2px',
            transform: 'scale(0.8)',
        },
        title: {
            fontSize: 14,
        },
        pos: {
            marginBottom: 12,
        },
    });

    const initialStateProfil = {
        id: null,
        libelle: "",
        temps: "",
        variation: "",
        actif_profil: [],
        conseil_profil: [],
        description: ""
    };

    const [profil, setProfil] = useState(initialStateProfil);
    const [allProfils, setAllProfils] = useState([]);

    const classes = useStyles();

    useEffect(() => {
        console.log(props.ResultatProfil)
        getProfil()
        getAllProfil()
    }, [])

    const getProfil = () => {
        ProfilDataService.getById(props.ResultatProfil)
            .then(response => {
                setProfil({
                    id: response.data.id,
                    libelle: response.data.libelle,
                    temps: response.data.temps,
                    variation: response.data.variation,
                    actif_profil: response.data.actif_profil,
                    description: response.data.description
                });
            })
            .catch(e => {
                console.log(e)
            })
    }


    const getAllProfil = () => {
        ProfilDataService.getAll()
            .then(response => {
                console.log(response.data)
                setAllProfils(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }

    return (
        <div>
            <div>
                Vous êtes de profil : {profil.libelle}
            </div>

            {allProfils.map((profil, key) => {
                console.log(profil)
                return (
                    <Card key={key} className={classes.root}>
                        <CardContent>
                            <Typography className={classes.title} color="textSecondary" gutterBottom>
                                {profil.libelle}
                            </Typography>

                            <Typography variant="body2" component="p">
                                Variation moyenne annuelle cible : {profil.variation}
                                <br />
                                Temps à y consacrer (Sauf gestion déléguée) : {profil.temps}
                            </Typography>
                        </CardContent>
                    </Card>
                )
            })}

        </div>
    );
};

export default ModalProfil;
